function codehap_image($type, $path, $location)
{

    if ($type == 1)
    {

        move_uploaded_file($path, $location);
    }

    if ($type == 2)
    {
        $downloadFile = fopen($location, "w");
        $handle = curl_init($path);
        curl_setopt($handle, CURLOPT_FILE, $downloadFile);
        curl_setopt($handle, CURLOPT_FOLLOWLOCATION, true);
        curl_exec($handle);
        curl_close($handle);
        fclose($downloadFile);
    }
}

function codehap_hash($length)
{
    $chars = '0123456789abcdefghijklmnopqrstuvwxyz';
    return substr(str_shuffle($chars) , 0, $length);
}


function codehap_seo($string)
{
    $string = str_replace(array(
        '[\', \']',
    ) , '', $string);
    $string = preg_replace('/\[.*\]/U', '', $string);
    $string = preg_replace('/&(amp;)?#?[a-z0-9]+;/i', '-', $string);
    $string = htmlentities($string, ENT_COMPAT, 'utf-8');
    $string = str_replace('quot', '', $string);
    $string = preg_replace(array(
        '/[^a-z0-9]/i',
        '/[-]+/',
    ) , '-', $string);
    return strtolower(trim($string, '-'));
}

function codehap_urls($text)
{
    $separator = "-";
    $text = str_replace(',', '', $text);
    $text = str_replace('(', '', $text);
    $text = str_replace(')', '', $text);
    $text = strtolower(trim($text, ' '));
    $text = str_replace(' ', '-', $text);
    $text = str_replace('_', '-', $text);
    $text = preg_replace('/-+/', '-', $text);
    $text = strtolower(trim($text, '-'));
    $text = trim(preg_replace('/-+/', '-', $text) , '-');
    return $text;
}

function codehap_slugify($string)
{

    $string = iconv('UTF-8', 'ASCII//TRANSLIT', $string);
    $string = preg_replace('/[^a-z0-9- ]/i', '', $string);
    $string = str_replace(' ', '-', $string);
    $string = trim(preg_replace('/-+/', '-', $string) , '-');
    $string = trim($string, '-');
    $string = strtolower($string);
    if (empty($string))
    {
        return 'n-a';
    }
    return $string;
}

function codehap_getCanonicalUrl()
{
    $url = $_SERVER['HTTP_HOST'] . $_SERVER['REQUEST_URI'];
    $url = "https://$url";
    $url = str_replace("www.", "", $url);
    $url = rtrim($url, "/");
    return $url;
}

function codehap_ucwords($text)
{
    $text = strtolower($text);
    $text = preg_replace("/\r|\n/", " ", $text);
    $text = trim(preg_replace('/\s+/', ' ', $text));
    $text = preg_replace('/[\s]+/', ' ', $text);
    $text = ucwords($text);
    return $text;
}


